import React, { Component } from 'react';
import PropTypes from 'prop-types';
import { connect } from 'react-redux';
import { fetchCurrenciesAll } from '../redux/actions';

const INITIAL_STATE = {
  value: '',
  description: '',
  currency: 'USD',
  method: '',
  tag: '',
};

class WalletForm extends Component {
  // componentDidMount() {
  //   const { dispatch } = this.props;
  //   dispatch(fetchCurrenciesAll());
  // }
  state = INITIAL_STATE;

  handleSubmit = (event) => {
    event.preventDefault();
    const { dispatch } = this.props;

    dispatch(fetchCurrenciesAll(this.state));
    this.setState(INITIAL_STATE);
  };

  handleChange = ({ target: { name, value } }) => {
    this.setState({
      [name]: value,
    });
  };

  render() {
    const { currencies, fethiching } = this.props;
    const { value, description, currency, method, tag } = this.state;
    return (
      <form onSubmit={ this.handleSubmit }>
        <label htmlFor="value-input">
          <input
            type="text"
            placeholder="Valor de despesa"
            data-testid="value-input"
            name="value-input"
            onChange={ this.handleChange }
            value={ value }
          />
        </label>
        <label htmlFor="description-input">
          <input
            type="text"
            placeholder="Descrição da despesa"
            data-testid="description-input"
            name="description-input"
            onChange={ this.handleChange }
            value={ description }
          />
        </label>
        <label htmlFor="currency-input">
          Moedas
          <select
            name="currency-input"
            data-testid="currency-input"
            id="currency-input"
            onChange={ this.handleChange }
            value={ currency }
          >
            { fethiching ? <option>Loading...</option> : (
              currencies.map((curr) => <option key={ curr }>{ curr }</option>))}
          </select>
        </label>
        <label htmlFor="method-input">
          Forma de pagamento:
          <select
            name="method-input"
            data-testid="method-input"
            onChange={ this.handleChange }
            value={ method }
          >
            <option>Dinheiro</option>
            <option>Cartão de crédito</option>
            <option>Cartão de débito</option>
          </select>
        </label>
        <label htmlFor="tag-input">
          <select
            name="tag-input"
            data-testid="tag-input"
            onChange={ this.handleChange }
            value={ tag }
          >
            <option>Alimentação</option>
            <option>Lazer</option>
            <option>Trabalho</option>
            <option>Transporte</option>
            <option>Saúde</option>
          </select>
        </label>
        <button type="submit">
          Adicionar despesa
        </button>
      </form>
    );
  }
}

const mapStateToProps = (state) => ({
  fethiching: state.wallet.fethiching,
  currencies: state.wallet.currencies,
});

export default connect(mapStateToProps)(WalletForm);

WalletForm.propTypes = {
  dispatch: PropTypes.func.isRequired,
  fethiching: PropTypes.bool.isRequired,
  currencies: PropTypes.arrayOf(PropTypes.string).isRequired,
};
