import fechAPIAll from '../../requestAPI';

export const SAVE_LOGIN = 'SAVE_LOGIN';
export const REQUEST_CURRENCIES = 'REQUEST_CURRENCIES';
export const SUCESS_CURRENCIES = 'SUCESS_CURRENCIES';
export const FAIL_CURRENCIES = 'FAIL_CURRENCIES';
export const EXPENSES_COTATION_SUCESS = 'EXPENSES_COTATION_SUCESS';
export const SAVE_EXPENSES_STORE = 'SAVE_EXPENSES_STORE';

export const saveLogin = (email) => ({
  type: SAVE_LOGIN,
  email,
});

export const requestCurrencies = () => ({
  type: REQUEST_CURRENCIES,
});

export const sucessCurrencies = (currencies) => ({
  type: SUCESS_CURRENCIES,
  currencies,
});

export const failCurrencies = (error) => ({
  type: FAIL_CURRENCIES,
  error,
});

export const expensesCotationSucess = (pay) => ({
  type: EXPENSES_COTATION_SUCESS,
  pay,
});

export const saveExpensesStore = (expense) => ({
  type: SAVE_EXPENSES_STORE,
  expense,
});

export const fetchCurrenciesAll = () => async (dispatch) => {
  dispatch(requestCurrencies());
  const responseFetch = await fechAPIAll();

  try {
    dispatch(sucessCurrencies(Object.keys(responseFetch).filter((sg) => sg !== 'USDT')));
  } catch (error) {
    dispatch(failCurrencies(error));
  }
};

export const saveExpenses = (expense) => async (dispatch) => {
  const responseFetch = await fechAPIAll();
  try {
    dispatch(saveExpensesStore({ ...expense, exchangeRates: responseFetch }));
  } catch (error) {
    dispatch(failCurrencies(error));
  }
};
