import React, { Component } from 'react';
import { connect } from 'react-redux';
import PropTypes from 'prop-types';

class Header extends Component {
  render() {
    const { expense, email } = this.props;
    const totExpenses = expense.map((itens) => {
      const { value, currency, exchangeRates } = itens;
      const { ask } = exchangeRates[currency];
      return ask * value;
    });

    const total = expense.length > 0;
    const response = total ? Math
      .round(totExpenses.reduce((acc, curr) => acc + curr) * 100) / 100 : 0;
    const valueDespesas = `Despesas: ${response}`;

    return (
      <header>
        <h4 data-testid="email-field">{`Email: ${email}`}</h4>
        <label htmlFor="total-field">
          Despesas:
          <h4 data-testid="total-field">{ valueDespesas }</h4>
        </label>
        <h4 data-testid="header-currency-field">BRL</h4>
      </header>
    );
  }
}

const mapStateToProps = (state) => ({
  email: state.user.email,
  expenses: state.wallet.expenses,
});

export default connect(mapStateToProps)(Header);

Header.propTypes = {
  email: PropTypes.string.isRequired,
  expense: PropTypes.arrayOf(PropTypes.shape()).isRequired,
};
