import React, { Component } from 'react';
import { connect } from 'react-redux';
import PropTypes from 'prop-types';

class Header extends Component {
  render() {
    const { expenses, email } = this.props;
    const totExpenses = expenses.reduce((acc, curr) => {
      let total = acc;
      total += parseFloat(curr.value) * curr.exchangeRates[curr.currency].ask;
      return total;
    }, 0);

    return (
      <header>
        <h4 data-testid="email-field">{`Email: ${email}`}</h4>
        <label htmlFor="total-field">
          Despesas:
          <h4 data-testid="total-field">{ totExpenses.toFixed(2)}</h4>
        </label>
        <h4 data-testid="header-currency-field">BRL</h4>
      </header>
    );
  }
}

const mapStateToProps = (state) => ({
  email: state.user.email,
});

export default connect(mapStateToProps)(Header);

Header.propTypes = {
  email: PropTypes.string.isRequired,
  expenses: PropTypes.arrayOf(PropTypes.shape()).isRequired,
};
