import React, { Component } from 'react';
import PropTypes from 'prop-types';
import { connect } from 'react-redux';
import { fetchCurrenciesAll, requestRates } from '../redux/actions';

const INITIAL_STATE = {
  value: '',
  description: '',
  currency: 'USD',
  method: 'Dinheiro',
  tag: 'Alimentação',
};

class WalletForm extends Component {
  state = { ...INITIAL_STATE };

  componentDidMount() {
    const { getListThunk } = this.props;
    getListThunk();
  }

  handleSubmitButton = async (event) => {
    event.preventDefault();
    const { expenses, dispatch } = this.props;
    const id = expenses.length;
    const exchangeRates = await fetchCurrenciesAll();
    dispatch(requestRates({ ...this.state, id, exchangeRates }));

    this.setState({ ...INITIAL_STATE });
  };

  handleChange = ({ target: { name, value } }) => {
    this.setState({
      [name]: value,
    });
  };

  render() {
    const { currencies, fethiching } = this.props;
    const { value, description, currency, method, tag } = this.state;
    return (
      <form>
        <label htmlFor="value-input">
          <input
            type="text"
            placeholder="Valor de despesa"
            data-testid="value-input"
            name="value-input"
            onChange={ this.handleChange }
            value={ value }
          />
        </label>
        <label htmlFor="description-input">
          <input
            type="text"
            placeholder="Descrição da despesa"
            data-testid="description-input"
            name="description-input"
            onChange={ this.handleChange }
            value={ description }
          />
        </label>
        <label htmlFor="currency-input">
          Moedas
          <select
            name="currency-input"
            data-testid="currency-input"
            id="currency-input"
            onChange={ this.handleChange }
            value={ currency }
          >
            { fethiching ? <option>Loading...</option> : (
              currencies.map((option, index) => (
                <option
                  key={ index }
                  value={ option }
                >
                  { option }
                </option>))
            )}
          </select>
        </label>
        <label htmlFor="method-input">
          Forma de pagamento:
          <select
            name="method-input"
            data-testid="method-input"
            onChange={ this.handleChange }
            value={ method }
          >
            <option value="Dinheiro">Dinheiro</option>
            <option value="Crédito">Cartão de crédito</option>
            <option value="Débito">Cartão de débito</option>
          </select>
        </label>
        <label htmlFor="tag-input">
          <select
            name="tag-input"
            data-testid="tag-input"
            onChange={ this.handleChange }
            value={ tag }
          >
            <option value="Alimentação">Alimentação</option>
            <option value="Lazer">Lazer</option>
            <option value="Trabalho">Trabalho</option>
            <option value="Transporte">Transporte</option>
            <option value="Saúde">Saúde</option>
          </select>
        </label>
        <button type="submit" onClick={ this.handleSubmitButton }>
          Adicionar despesa
        </button>
      </form>
    );
  }
}

const mapStateToProps = (state) => ({
  fethiching: state.wallet.fethiching,
  currencies: state.wallet.currencies,
  expenses: state.wallet.expenses,
});

const mapDispatchToProps = (dispatch) => ({
  getListThunk: () => dispatch(fetchCurrenciesAll()),
  getExpense: (expense) => dispatch(requestRates(expense)),
});

export default connect(mapStateToProps, mapDispatchToProps)(WalletForm);

WalletForm.propTypes = {
  expenses: PropTypes.arrayOf(PropTypes.shape()).isRequired,
  dispatch: PropTypes.func.isRequired,
  fethiching: PropTypes.bool.isRequired,
  currencies: PropTypes.arrayOf(PropTypes.string).isRequired,
  getListThunk: PropTypes.func.isRequired,
};
