import React, { Component } from 'react';
import { connect } from 'react-redux';

class Table extends Component {
  render() {
    const { expenses } = this.props;
    const validationExpenses = !expenses;
    const renderTable = expenses.map((expenses) => {
      const { description, tag, method, value, exchangeRates, currency, id } = expenses;
      const coinName = exchangeRates[currency];
      const somaTot = Number(value) * Number(coinName.ask);
      const totRedond = somaTot.toFixed(2);
      const totCoin = coinName.toFixed(2);
      return (
        <tr key={ id }>
          <th>{ description }</th>
          <th>{ tag }</th>
          <th>{ method }</th>
          <th>{ value.toFixed(2) }</th>
          <th>{ coinName.name }</th>
          <th>{ totCoin }</th>
          <th>{ totRedond }</th>
          <th>Real</th>
          <th><button type="button"> Deletar </button></th>
        </tr>
      );
    });

    return (
      <table>
        <tr>
          <th>Descrição</th>
          <th>Tag</th>
          <th>Método de pagamento</th>
          <th>Valor</th>
          <th>Moeda</th>
          <th>Câmbio utilizado</th>
          <th>Valor convertido</th>
          <th>Moeda de conversão</th>
          <th>Editar/Excluir</th>
        </tr>
        <tbody>
          { validationExpenses ? '' : renderTable}
        </tbody>
      </table>
    );
  }
}

const mapStateToProps = (state) => ({
  expenses: state.wallet.expenses,
});

export default connect(mapStateToProps)(Table);
