import React, { Component } from 'react';
import PropTypes from 'prop-types';
import { connect } from 'react-redux';
import { fetchCurrenciesAll, saveExpensesStore } from '../redux/actions';

const INITIAL_STATE = {
  inpValue: '',
  inpDescription: '',
  inpCurrency: 'USD',
  inpMethod: 'Dinheiro',
  inpTag: 'Alimentação',
};

class WalletForm extends Component {
  state = INITIAL_STATE;

  componentDidMount() {
    const { dispatch } = this.props;
    dispatch(fetchCurrenciesAll());
  }

  handleSubmit = async (event) => {
    event.preventDefault();
    const { dispatch, expenses } = this.props;
    const {
      inpValue,
      inpDescription,
      inpCurrency,
      inpMethod,
      inpTag,
    } = this.state;

    const calculatorID = expenses.length !== 0 ? expenses[expenses.length - 1].id + 1 : 0;
    const expense = {
      id: calculatorID,
      value: inpValue,
      description: inpDescription,
      currency: inpCurrency,
      method: inpMethod,
      tag: inpTag,
    };

    dispatch(saveExpensesStore(expense));
    this.setState(INITIAL_STATE);
  };

  handleChange = ({ target: { name, value } }) => {
    this.setState({
      [name]: value,
    });
  };

  render() {
    const { currencies, fethiching } = this.props;
    const { inpValue, inpDescription } = this.state;
    return (
      <form onSubmit={ this.handleSubmit }>
        <label htmlFor="value-input">
          <input
            type="text"
            placeholder="Valor de despesa"
            data-testid="value-input"
            name="value-input"
            onChange={ this.handleChange }
            value={ inpValue }
          />
        </label>
        <label htmlFor="description-input">
          <input
            type="text"
            placeholder="Descrição da despesa"
            data-testid="description-input"
            name="description-input"
            onChange={ this.handleChange }
            value={ inpDescription }
          />
        </label>
        <label htmlFor="currency-input">
          Moedas
          <select
            name="currency-input"
            data-testid="currency-input"
            id="currency-input"
            onChange={ this.handleChange }
          >
            { fethiching ? <option>Loading...</option> : (
              currencies.map((curr) => (
                <option
                  key={ curr }
                  value={ curr }
                >
                  { curr }
                </option>))
            )}
          </select>
        </label>
        <label htmlFor="method-input">
          Forma de pagamento:
          <select
            name="method-input"
            data-testid="method-input"
            onChange={ this.handleChange }
          >
            <option value="Dinheiro">Dinheiro</option>
            <option value="Crédito">Cartão de crédito</option>
            <option value="Débito">Cartão de débito</option>
          </select>
        </label>
        <label htmlFor="tag-input">
          <select
            name="tag-input"
            data-testid="tag-input"
            onChange={ this.handleChange }
          >
            <option value="Alimentação">Alimentação</option>
            <option value="Lazer">Lazer</option>
            <option value="Trabalho">Trabalho</option>
            <option value="Transporte">Transporte</option>
            <option value="Saúde">Saúde</option>
          </select>
        </label>
        <button type="submit" onClick={ this.handleSubmit }>
          Adicionar despesa
        </button>
      </form>
    );
  }
}

const mapStateToProps = (state) => ({
  fethiching: state.wallet.fethiching,
  currencies: state.wallet.currencies,
  expenses: state.wallet.expenses,
});

export default connect(mapStateToProps)(WalletForm);

WalletForm.propTypes = {
  expenses: PropTypes.arrayOf(PropTypes.shape()).isRequired,
  dispatch: PropTypes.func.isRequired,
  fethiching: PropTypes.bool.isRequired,
  currencies: PropTypes.arrayOf(PropTypes.string).isRequired,
};
